export function sendShardMessage(gateway, shard, message, highPriority = false) {
    if (typeof shard === "number")
        shard = gateway.shards.get(shard);
    if (!shard)
        return;
    if (highPriority)
        shard.queue.unshift(message);
    else
        shard.queue.push(message);
    gateway.processGatewayQueue(gateway, shard.id);
}
