/** Get a guild scheduled event. */
export async function getScheduledEvent(bot, guildId, eventId, options) {
    const event = await bot.rest.runMethod(bot.rest, "get", `${bot.constants.endpoints.GUILD_SCHEDULED_EVENT(guildId, eventId)}?with_user_count=${options?.withUserCount ?? false}`);
    return bot.transformers.scheduledEvent(bot, event);
}
