/** Edits command permissions for a specific command for your application in a guild. */
export async function editApplicationCommandPermissions(bot, guildId, commandId, options) {
    const result = await bot.rest.runMethod(bot.rest, "put", bot.constants.endpoints.COMMANDS_PERMISSION(bot.applicationId, guildId, commandId), {
        permissions: options,
    });
    return bot.transformers.applicationCommandPermission(bot, result);
}
