/** Returns a previously-sent webhook message from the same token. Returns a message object on success. */
export async function getWebhookMessage(bot, webhookId, webhookToken, messageId, options) {
    let url = bot.constants.endpoints.WEBHOOK_MESSAGE(webhookId, webhookToken, messageId);
    // QUERY PARAMS
    if (options?.threadId) {
        url += `?thread_id=${options.threadId}`;
    }
    const result = await bot.rest.runMethod(bot.rest, "get", url);
    return bot.transformers.message(bot, result);
}
